// Tab functionality
// Generated by ChatGPT, edited
function openTab(tabId) {
    // Hide all tab content
    const tabContents = document.querySelectorAll('.tab-content');
    tabContents.forEach(content => content.style.display = 'none');

    // Remove active class from all tab buttons
    const tabButtons = document.querySelectorAll('.tab-button');
    tabButtons.forEach(button => button.classList.remove('active'));

    // Show the selected tab content
    document.getElementById(tabId).style.display = 'flex';

    // Add active class to the clicked tab button
    document.querySelector(`.tab-button[onclick="openTab('${tabId}')"]`).classList.add('active');
}




// In-world chat functionality
// Generated by chatGPT, edited to work with in-world chat 

// Function to add a system message
function addInWorldSystemMessage(text) {
    const message = document.createElement('div');
    message.className = 'system-message';
    message.textContent = text;
    document.getElementById('inWorldChatMessages').appendChild(message);
}

// Function to add a user header
function addInWorldUserHeader(userName, userImage) {
    const header = document.createElement('div');
    header.className = 'user-header';

    const img = document.createElement('img');
    img.src = userImage;
    img.alt = userName;
    img.className = 'user-image';

    const name = document.createElement('span');
    name.className = 'user-name';
    name.textContent = userName;

    header.appendChild(img);
    header.appendChild(name);

    document.getElementById('inWorldChatMessages').appendChild(header);
}

// Function to add a user message
function addInWorldUserMessage(text) {
    const message = document.createElement('div');
    message.className = 'user-message';
    message.textContent = text;
    document.getElementById('inWorldChatMessages').appendChild(message);
}

// Example usage:
addInWorldSystemMessage("Welcome to the chat!");
addInWorldUserHeader("Greg", "../tokenium/images/Dave.png");
addInWorldUserMessage("Guten tagch pico!");
addInWorldUserMessage("How are not you?");
addInWorldUserHeader("Ivan", "../tokenium/images/Dave.png");
addInWorldUserMessage("Not me is good, thanks!");
addInWorldSystemMessage("Server will go on strike!");